operation.add:
			//Adding fastcheckout
			if ($this->config->get('multisafepay_account_type') == 'fastcheckout') {
				$data['multisafepay_fastcheckout'] = $this->url->link('payment/multisafepay-fastcheckout');
			} else {
				$data['multisafepay_fastcheckout'] = '';
			}
			if ($this->config->get('multisafepay_enable_checkout_button_connect') == 'true') {
				$data['enable_checkout_button_connect'] = true;
				$data['multisafepay_fastcheckout'] = $this->url->link('payment/multisafepay-fastcheckout');
			} else {
				$data['enable_checkout_button_connect'] = false;
			}
			//Ending Fastcheckout

//Adding fastcheckout
if ($this->config->get('multisafepay_account_type') == 'fastcheckout') {
	$data['multisafepay_fastcheckout'] = $this->url->link('payment/multisafepay-fastcheckout');
} else {
	$data['multisafepay_fastcheckout'] = '';
}
if ($this->config->get('multisafepay_enable_checkout_button_connect') == 'true') {
	$data['enable_checkout_button_connect'] = true;
	$data['multisafepay_fastcheckout'] = $this->url->link('payment/multisafepay-fastcheckout');
} else {
	$data['enable_checkout_button_connect'] = false;
}
