operation.add:
				//Adding fastcheckout
           		$storeid = $this->config->get('config_store_id');
				if ($this->config->get('multisafepay_account_type_'.$storeid) == 'fastcheckout') {
					$data['multisafepay_fastcheckout'] = $this->url->link('payment/multisafepay-fastcheckout');
				} else {
					$data['multisafepay_fastcheckout'] = '';
				}
				if ($this->config->get('multisafepay_enable_checkout_button_connect_'.$storeid) ) {
					$data['enable_checkout_button_connect'] = true;
					$data['multisafepay_fastcheckout'] = $this->url->link('payment/multisafepay-fastcheckout');
				} else {
					$data['enable_checkout_button_connect'] = false;
				}
				//Ending Fastcheckout


           		$storeid = $this->config->get('config_store_id');
				if ($this->config->get('multisafepay_account_type_'.$storeid) == 'fastcheckout') {
					$data['multisafepay_fastcheckout'] = $this->url->link('payment/multisafepay-fastcheckout');
				} else {
					$data['multisafepay_fastcheckout'] = '';
				}
				if ($this->config->get('multisafepay_enable_checkout_button_connect_'.$storeid) ) {
					$data['enable_checkout_button_connect'] = true;
					$data['multisafepay_fastcheckout'] = $this->url->link('payment/multisafepay-fastcheckout');
				} else {
					$data['enable_checkout_button_connect'] = false;
				}
				//Ending Fastcheckout
